import React, { useRef, useEffect } from 'react';
import gsap from 'gsap';

const Login = () => {
  const formRef = useRef(null);
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const buttonRef = useRef(null);

  useEffect(() => {
    const form = formRef.current;
    const email = emailRef.current;
    const password = passwordRef.current;
    const button = buttonRef.current;

    const tl = gsap.timeline({ defaults: { ease: 'power3.out' } });

    tl.from(form, { opacity: 0, y: -50, duration: 0.6 })
      .from(email, { opacity: 0, x: -50, duration: 0.4 })
      .from(password, { opacity: 0, x: -50, duration: 0.4 }, '-=0.2')
      .from(button, { opacity: 0, x: -50, duration: 0.4 }, '-=0.2');
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your login logic here
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form ref={formRef} onSubmit={handleSubmit} className="login-form">
        <label>
          Email:
          <input ref={emailRef} type="email" required className="input-field" />
        </label>
        <label>
          Password:
          <input ref={passwordRef} type="password" required className="input-field" />
        </label>
        <button ref={buttonRef} type="submit" className="submit-button">Login</button>
      </form>
    </div>
  );
};

export default Login;
