import React, { useEffect, useState } from 'react';
import './NavBar.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const Headers = () => {
    const [userdata, setUserdata] = useState({});
    console.log("response", userdata);

    const getUser = async () => {
        try {
            const response = await axios.get("http://localhost:1000/login/success", { withCredentials: true });
            setUserdata(response.data.user);
        } catch (error) {
            console.log("error", error);
        }
    };

    const logout = () => {
        window.open("http://localhost:1000/logout", "_self");
    };

    useEffect(() => {
        getUser();
    }, []);

    return (
        <header>
            <nav>
                <div className="left">
                    <NavLink to="/home">
                        <img src='../Taskify.jpg' alt='Taskify Logo' />
                    </NavLink>
                </div>
                <div className="right">
                    <ul>
                        <li>
                            <NavLink to="/">
                                Home
                            </NavLink>
                        </li>
                        {Object.keys(userdata).length > 0 ? (
                            <>
                                <li style={{ color: "black", fontWeight: "bold" }}>{userdata.displayName}</li>
                                <li>
                                    <NavLink to="/dashboard">
                                        Dashboard
                                    </NavLink>
                                </li>
                                <li onClick={logout} style={{ cursor: "pointer" }}>Logout</li>
                                <li>
                                    <img src={userdata.image} style={{ width: "50px", borderRadius: "50%" }} alt={userdata.displayName} />
                                </li>
                            </>
                        ) : (
                            <li>
                                <NavLink to="/login">
                                    Login
                                </NavLink>
                            </li>
                        )}
                    </ul>
                </div>
            </nav>
        </header>
    );
};

export default Headers;
