import React, { useEffect, useState } from 'react';
import "./NavBar.css";  // Ensure this file has relevant styles for navigation
import axios from "axios";

const Dashboard = () => {
    const [userdata, setUserdata] = useState({});
    console.log("response", userdata);

    const getUser = async () => {
        try {
            const response = await axios.get("http://localhost:1000/login/success", { withCredentials: true });
            setUserdata(response.data.user);
        } catch (error) {
            console.log("error", error);
        }
    };

    useEffect(() => {
        getUser();
    }, []);

    return (
        <div className="profile-container">
            {Object.keys(userdata).length > 0 ? (
                <div className="profile-content">
                    <h1 className="profile-name">{userdata.displayName}</h1>
                    <h2 className="profile-email">{userdata.email}</h2>
                    <div className="profile-image-wrapper">
                        <img 
                            src={userdata.image} 
                            alt="User" 
                            className="profile-image" 
                        />
                    </div>
                </div>
            ) : (
                <div className="profile-placeholder">
                    <h2>No user data available</h2>
                </div>
            )}
        </div>
    );
}

export default Dashboard;
