import axios from 'axios';
import React, { useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
const Dashboard = () => {
    const [userdata, setUserdata] = useState({});
    const navigate = useNavigate();

    const getUser = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:1000/login/success", { withCredentials: true });
            console.log("response", response);
        } catch (error) {
            navigate("/dashboard");
        }
    }, [navigate]);

    useEffect(() => {
        getUser();
    }, [getUser]);

    return (
        <div style={{ textAlign: "center" } } className='profile'>
            <img src={userdata?.image} style={{ width: "50px", borderRadius: "50%" }} alt="" />
            <li style={{ color: "white", fontWeight: "bold" }}>{userdata?.displayName}</li>
            <li onClick={logout}>Logout</li>
        </div>
    );
};

export default Dashboard;
