import React, { useEffect, useState } from 'react';
import './NavBar.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const Headers = () => {
    const [userdata, setUserdata] = useState({});

    useEffect(() => {
        getUser();
    }, []);

    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:1000/login/sucess', { withCredentials: true });
            setUserdata(response.data.user);
        } catch (error) {
            console.log('error', error);
        }
    };

    const logout = () => {
        window.open('http://localhost:1000/logout', '_self');
    };

    return (
        <>
            <header>
                <nav>
                    <div className="logo">
                        <NavLink to="/">
                            <img src="../Taskify.jpg" alt="Img not Found" />
                        </NavLink>
                    </div>
                    <input type="checkbox" id="nav-toggle" className="nav-toggle" />
                    <label htmlFor="nav-toggle" className="nav-toggle-label">
                        <span></span>
                    </label>
                    <div className="nav-menu">
                        <ul>
                            <li><NavLink to="/">Home</NavLink></li>
                            {
                                Object.keys(userdata).length > 0 ? (
                                    <>
                                        <li><NavLink to="/dashboard">Dashboard</NavLink></li>
                                        <li>{userdata.displayName}</li>
                                        <li><img src={userdata.image} alt="" /></li>
                                        <li onClick={logout}>Logout</li>
                                    </>
                                ) : (
                                    <li><NavLink to="/login">Login</NavLink></li>
                                )
                            }
                        </ul>
                    </div>
                </nav>
            </header>
        </>
    );
};

export default Headers;
