import React, { useEffect, useState } from 'react'
import './Profile.css';
import { NavLink } from "react-router-dom"
import axios from "axios"

const Profile = () => {
    const [userdata, setUserdata] = useState({});
    console.log("response", userdata)


    const getUser = async () => {
        try {
            const response = await axios.get("http://localhost:1000/login/sucess", { withCredentials: true });

            setUserdata(response.data.user)
        } catch (error) {
            console.log("error", error)
        }
    }

    // logoout


    useEffect(() => {
        getUser()
    }, [])
    return (
        <>
            
                    <div className="right">
                        <ul>
                            
                            {
                                Object?.keys(userdata)?.length > 0 ? (
                                    <>
                               
                                        
                                        <li>
                                            <NavLink to="/todo">
                                                Home
                                            </NavLink>
                                        </li>
                                        
                                        <li style={{ color: "white", fontWeight: "bold" }}>Welcome {userdata?.displayName}</li>
                                        <li>
                                            <img src={userdata?.image} style={{ width: "50px", borderRadius: "50%" }} alt="" />
                                        </li>
                                        
                                    </>
                                ) : <li>
                                  
                                </li>
                            }



                        </ul>
                    </div>
                
            
        </>
    )
}

export default Profile
