import React, { useEffect, useState } from 'react';
import './Headers.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const Headers = () => {
    const [userdata, setUserdata] = useState({});
    const [error, setError] = useState(null);

    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:1000/login/success', { withCredentials: true });
            setUserdata(response.data.user);
        } catch (error) {
            setError('Failed to fetch user data');
            console.log('error', error);
        }
    };

    const logout = () => {
        window.open('http://localhost:1000/logout', '_self');
    };

    useEffect(() => {
        getUser();
    }, []);

    return (
        <header>
            <nav>
                <div className="left">
                    <NavLink to="/">
                        <img src='../Taskify.jpg' alt='Taskify Logo' />
                    </NavLink>
                </div>
                <div className="right">
                    <ul>
                        <li>
                            <NavLink to="/">Home</NavLink>
                        </li>
                        {userdata && Object.keys(userdata).length > 0 ? (
                            <>
                                <li style={{ color: 'black', fontWeight: 'bold' }}>{userdata.displayName}</li>
                                <li>
                                    <NavLink to="/dashboard">Dashboard</NavLink>
                                </li>
                                <li onClick={logout}>Logout</li>
                                <li>
                                    <img src={userdata.image} alt={userdata.displayName} />
                                </li>
                            </>
                        ) : (
                            <li>
                                <NavLink to="/login">Login</NavLink>
                            </li>
                        )}
                    </ul>
                    {error && <p className="error">{error}</p>}
                </div>
            </nav>
        </header>
    );
};

export default Headers;
