import axios from 'axios';
import React, { useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
const Dashboard = () => {
    const navigate = useNavigate();

    const getUser = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:1000/login/success", { withCredentials: true });
            console.log("response", response);
        } catch (error) {
            navigate("/dashboard");
        }
    }, [navigate]);

    useEffect(() => {
        getUser();
    }, [getUser]);

    return (
        <div style={{ textAlign: "center" } } className='profile'>
            <img src={userdata?.image} style={{ width: "50px", borderRadius: "50%" }} alt="" />
        </div>
    );
};

export default Dashboard;
 


const Headers = () => {
    const [userdata, setUserdata] = useState({});
    console.log("response", userdata)


    const getUser = async () => {
        try {
            const response = await axios.get("http://localhost:1000/login/sucess", { withCredentials: true });

            setUserdata(response.data.user)
        } catch (error) {
            console.log("error", error)
        }
    }

    // logoout
    const logout = () => {
        window.open("http://localhost:1000/logout", "_self")
    }

    useEffect(() => {
        getUser()
    }, [])
    return (
        <>
            <header>
                <nav>
                    <div className="left">
                        <NavLink to="/">
                            <img src='../Taskify.jpg' alt='Img not Found' style={{ height: "4em" }}  ></img>
                        </NavLink>

                    </div>
                    <div className="right">
                        <ul>
                            <li>
                                <NavLink to="/">
                                    Home
                                </NavLink>
                            </li>
                            {
                                Object?.keys(userdata)?.length > 0 ? (
                                    <>
                                        
                                        <li>
                                            <NavLink to="/dashboard">
                                                Dashboard
                                            </NavLink>
                                        </li>
                                        
                                        <li style={{ color: "white", fontWeight: "bold" }}>{userdata?.displayName}</li>
                                        <li>
                                            <img src={userdata?.image} style={{ width: "50px", borderRadius: "50%" }} alt="" />
                                        </li>
                                        <li onClick={logout}>Logout</li>
                                    </>
                                ) : <li>
                                    <NavLink to="/login">
                                        Login
                                    </NavLink>
                                </li>
                            }



                        </ul>
                    </div>
                </nav>
            </header>
        </>
    )
}
